struct Color
{
   Color(const color_t r, const color_t g, const color_t b, const color_t a) : r(r), g(g), b(b), a(a) {}
   Color(const color_t r, const color_t g, const color_t b) : Color(r, g, b, 255) {}

   color_t r;
   color_t g;
   color_t b;
   color_t a;

   bool operator==(const Color& other)
   {
      return r == other.r && g == other.g && b == other.b && a == other.a;
   }

   bool operator==(const Color& other) const
   {
      return r == other.r && g == other.g && b == other.b && a == other.a;
   }

   inline std::vector<float> normalizeToOne() const
   {
      return {
         static_cast<float>(r) / 255,
         static_cast<float>(g) / 255,
         static_cast<float>(b) / 255,
         static_cast<float>(a) / 255,
      };
   }
};

