class VisPerspective
{
public:
   VisPerspective() = default;

   inline std::string serialize() const
   {
      return "Perspective [(x=" + std::to_string(camera_x_) + ", y=" + std::to_string(camera_y_) + ", z=" + std::to_string(camera_z_) + "),"
         + " <rot_x=" + std::to_string(camera_rotation_x_) + ", rot_y=" + std::to_string(camera_rotation_y_) + ", rot_z=" + std::to_string(camera_rotation_z_) + ">]"
         + " <disp_x=" + std::to_string(display_window_x_) + ", disp_y=" + std::to_string(display_window_y_) + ", disp_z=" + std::to_string(display_window_z_) + ">]"
         ;
   }

   inline void changedRotationX() const { changed_rotation_x_ = true; }
   inline void changedRotationY() const { changed_rotation_y_ = true; }
   inline void changedRotationZ() const { changed_rotation_z_ = true; }
   inline void changedDisplay() const { changed_display_ = true; }

   inline float getCameraX() const { return camera_x_; }
   inline float getCameraY() const { return camera_y_; }
   inline float getCameraZ() const { return camera_z_; }
   inline float getRotationCameraX() const { return camera_rotation_x_; }
   inline float getRotationCameraY() const { return camera_rotation_y_; }
   inline float getRotationCameraZ() const { return camera_rotation_z_; }
   inline float getDisplayWindowX() const { return display_window_x_; }
   inline float getDisplayWindowY() const { return display_window_y_; }
   inline float getDisplayWindowZ() const { return display_window_z_; }
   inline void setCameraX(const float val) { camera_x_ = val; }
   inline void setCameraY(const float val) { camera_y_ = val; }
   inline void setCameraZ(const float val) { camera_z_ = val; }
   inline void setCameraRotationX(const float val) { camera_rotation_x_ = val; changedRotationX(); }
   inline void setCameraRotationY(const float val) { camera_rotation_y_ = val; changedRotationY(); }
   inline void setCameraRotationZ(const float val) { camera_rotation_z_ = val; changedRotationZ(); }
   inline void setDisplayWindowX(const float val) { display_window_x_ = val; changedDisplay(); }
   inline void setDisplayWindowY(const float val) { display_window_y_ = val; changedDisplay(); }
   inline void setDisplayWindowZ(const float val) { display_window_z_ = val; changedDisplay(); }

   inline float getRotationCameraXSin() const
   { 
      if (changed_rotation_x_) { 
         camera_rotation_x_sin_ = std::sin(camera_rotation_x_); 
         camera_rotation_x_cos_ = std::cos(camera_rotation_x_); 
         changed_rotation_x_ = false; 
      } 

      return camera_rotation_x_sin_; 
   }
   
   inline float getRotationCameraYSin() const
   { 
      if (changed_rotation_y_) { 
         camera_rotation_y_sin_ = std::sin(camera_rotation_y_); 
         camera_rotation_y_cos_ = std::cos(camera_rotation_y_); 
         changed_rotation_y_ = false;
      } 
      
      return camera_rotation_y_sin_; 
   }
   
   inline float getRotationCameraZSin() const
   {
      if (changed_rotation_z_) { 
         camera_rotation_z_sin_ = std::sin(camera_rotation_z_); 
         camera_rotation_z_cos_ = std::cos(camera_rotation_z_); 
         changed_rotation_z_ = false;
      } 
      
      return camera_rotation_z_sin_; 
   }
   
   inline float getRotationCameraXCos() const
   { 
      if (changed_rotation_x_) { 
         camera_rotation_x_sin_ = std::sin(camera_rotation_x_);
         camera_rotation_x_cos_ = std::cos(camera_rotation_x_);
         changed_rotation_x_ = false;
      } 
      
      return camera_rotation_x_cos_; 
   }
   
   inline float getRotationCameraYCos() const
   { 
      if (changed_rotation_y_) { 
         camera_rotation_y_sin_ = std::sin(camera_rotation_y_);
         camera_rotation_y_cos_ = std::cos(camera_rotation_y_);
         changed_rotation_y_ = false;
      } 
      
      return camera_rotation_y_cos_; 
   }
   
   inline float getRotationCameraZCos() const
   { 
      if (changed_rotation_z_) { 
         camera_rotation_z_sin_ = std::sin(camera_rotation_z_);
         camera_rotation_z_cos_ = std::cos(camera_rotation_z_);
         changed_rotation_z_ = false;
      } 
      
      return camera_rotation_z_cos_; 
   }

   inline Pln3D getDisplayPlane() const
   {
      if (changed_display_) {
         display_plane_.base_point_.x = display_window_x_;
         display_plane_.base_point_.y = display_window_y_;
         display_plane_.base_point_.z = display_window_z_;
      }

      return display_plane_;
   }

private:
   float camera_x_{};
   float camera_y_{};
   float camera_z_{};

   float camera_rotation_x_{};
   float camera_rotation_y_{};
   float camera_rotation_z_{};

   mutable float camera_rotation_x_sin_{};
   mutable float camera_rotation_y_sin_{};
   mutable float camera_rotation_z_sin_{};
 
   mutable float camera_rotation_x_cos_{};
   mutable float camera_rotation_y_cos_{};
   mutable float camera_rotation_z_cos_{};

   mutable bool changed_rotation_x_{ true };
   mutable bool changed_rotation_y_{ true };
   mutable bool changed_rotation_z_{ true };

   mutable bool changed_display_{ true };

   float display_window_x_{};
   float display_window_y_{};
   float display_window_z_{};

   mutable Pln3D display_plane_{ { 0, 0, 0 }, CAMERA_PLANE_IN_CAMERA_COORDINATES.normal_vector_ };
};

class VisTranslator : public Failable
{
public:
   inline VisTranslator() : Failable("VisTranslator") {}

   inline Vec2D translate(const Vec2D& point)
   {
      return translate({ point.x, point.y, 0 });
   }

   inline Vec2D translate(const Vec3D& point)
   {
      return translateCore(point);
   }

   inline Vec3D reverseTranslate(const Vec2D& point)
   {
      return reverseTranslateCore(point);
   }

   inline Pol2D translatePolygon(const Pol3D& pol)
   {
      return translatePolygonCore(pol);
   }

   inline Pol2D reverseTranslate(const Pol2D& pol)
   {
      Pol2D result{};

      for (const auto& point : pol.points_) {
         result.add(reverseTranslate(point).projectToXY());
      }

      return result;
   }

   inline void setPerspective(const std::shared_ptr<VisPerspective> perspective)
   {
      perspective_ = perspective;
   }

   inline std::shared_ptr<VisPerspective> getPerspective() const
   {
      return perspective_;
   }

private:
   std::shared_ptr<VisPerspective> perspective_{ std::make_shared<VisPerspective>() };

   virtual Vec2D translateCore(const Vec3D& point) = 0;
   virtual Vec3D reverseTranslateCore(const Vec2Df& point) = 0;

   inline virtual Pol2D translatePolygonCore(const Pol3D& pol)
   {
      Pol2D result{};

      for (const auto& point : pol.points_) {
         auto current_point{ translate(point) };

         if (!std::isnan(current_point.x) && !std::isnan(current_point.y)) {
            result.add(current_point);
         }
      }

      return result;
   }
};

class DefaultTranslator : public VisTranslator
{
private:
   inline Vec2D translateCore(const Vec3D& point) override { return point.projectToXY(); }
   inline Vec3D reverseTranslateCore(const Vec2D& point) override { return { point.x, point.y, 0 }; }
};

enum class AddCorrectExtensionToFileName
{
   Never,     // E.g., for png: "filename" -> "filename"; "filename.png" -> "filename.png".
   IfMissing, // E.g., for png: "filename" -> "filename.png"; "filename.png" -> "filename.png".     (DEFAULT)
   Always,    // E.g., for png: "filename" -> "filename.png"; "filename.png" -> "filename.png.png".
};

